sqlMap.select:
/* optionalBoardDAO.selectCmmntBoardArticleList */
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM ( SELECT a.NTT_ID , a.NTT_SJ , a.NTT_CN , a.FRST_REGISTER_ID , a.NTCR_NM , a.NTT_NO , a.INQIRE_CO , a.PARNTS , a.REPLY_AT , nvl(a.REPLY_LC,0) as REPLY_LC , a.USE_AT , a.ATCH_FILE_ID , a.BBS_ID , a.NTCE_BGNDE , a.NTCE_ENDDE , a.SECRET_AT as secret , NVL(c.COMMENT_CNT,0) AS comment_cnt , TO_CHAR(a.FRST_REGIST_PNTTM, 'YYYY-MM-DD') as FRST_REGISTER_PNTTM , a.FRST_REGIST_PNTTM as reg_date , a.SECTION , a.HTML_AT HTML_YN , a.DELETE_CODE DELCODE , a.DEPT_ID FROM $tableNm$ a LEFT OUTER JOIN ( SELECT BBS_ID, NTT_ID, COUNT(*) AS COMMENT_CNT FROM TB_BBS_CMMNT GROUP BY BBS_ID, NTT_ID ) c on a.BBS_ID = c.BBS_ID AND a.NTT_ID = c.NTT_ID WHERE a.BBS_ID = #bbsId#
ORDER BY a.PARNTS desc, a.REPLY_LC ASC
) ALL_LIST ) WHERE RNUM > #firstIndex# AND RNUM <= #firstIndex# + #recordCountPerPage#

/* optionalBoardDAO.selectPrevNexArticle */
SELECT ntt_id, nvl(prev_ntt_id, 0) as prev_ntt_id, nvl(next_ntt_id, 0) as next_ntt_id, prev_ntt_sj, next_ntt_sj FROM ( SELECT NTT_ID , LEAD(ntt_id, 1, '') OVER (ORDER BY FRST_REGIST_PNTTM DESC) AS prev_ntt_id , LAG(ntt_id, 1, '') OVER (ORDER BY FRST_REGIST_PNTTM DESC ) AS next_ntt_id , LEAD(ntt_sj, 1, '') OVER (ORDER BY FRST_REGIST_PNTTM DESC) AS prev_ntt_sj , LAG(ntt_sj, 1, '') OVER (ORDER BY FRST_REGIST_PNTTM DESC ) AS next_ntt_sj FROM $tableNm$ a WHERE BBS_ID = #bbsId# AND a.DELETE_CODE = '0'
) WHERE ntt_id = #nttId#

/* optionalBoardDAO.prevSelectPrevNexArticle */
SELECT aa.NTT_TYPE, aa.NTT_ID, aa.NTT_SJ FROM ( SELECT 'NEXT' AS ntt_type ,a.NTT_ID ,a.NTT_SJ FROM $tableNm$ a WHERE a.BBS_ID = #bbsId# AND a.DELETE_CODE = '0' AND a.NTT_ID IN ( SELECT MIN(NTT_ID) FROM $tableNm$ WHERE BBS_ID = #bbsId# AND DELETE_CODE = '0' AND NTT_ID > #nttId#
)
UNION ALL
SELECT 'PREV' AS ntt_type ,a.NTT_ID ,a.NTT_SJ FROM $tableNm$ a WHERE a.BBS_ID = #bbsId# AND a.DELETE_CODE = '0' AND a.NTT_ID IN ( SELECT MAX(NTT_ID) FROM $tableNm$ WHERE BBS_ID = #bbsId# AND DELETE_CODE = '0' AND NTT_ID < #nttId#
) ) aa

/* optionalBoardDAO.selectPrevArticle */
SELECT * FROM (SELECT ROWNUM RNUM, a.* FROM (SELECT ntt_id, ntt_id AS prev_ntt_id, bbs_id, ntt_sj, NTT_TYPE, FRST_REGIST_PNTTM, DELETE_CODE, CASE WHEN REPLACE(NTCE_BGNDE,'-','') > TO_CHAR(SYSDATE, 'YYYYMMDD') THEN '1' WHEN REPLACE(NTCE_BGNDE,'-','') <= TO_CHAR(SYSDATE, 'YYYYMMDD') AND REPLACE(NTCE_ENDDE,'-','') >= TO_CHAR(SYSDATE, 'YYYYMMDD') THEN '2' ELSE '3' END AS STATUS FROM tb_bbs_estn a WHERE DELETE_CODE = '0' AND ntt_id > #nttId# AND bbs_id = #bbsId#
ORDER BY FRST_REGIST_PNTTM ) a )a WHERE A.RNUM = 1

/* optionalBoardDAO.selectNextArticle */
SELECT * FROM (SELECT ROWNUM RNUM, a.* FROM (SELECT ntt_id, ntt_id AS next_ntt_id, bbs_id, ntt_sj, FRST_REGIST_PNTTM, DELETE_CODE, CASE WHEN REPLACE(NTCE_BGNDE,'-','') > TO_CHAR(SYSDATE, 'YYYYMMDD') THEN '1' WHEN REPLACE(NTCE_BGNDE,'-','') <= TO_CHAR(SYSDATE, 'YYYYMMDD') AND REPLACE(NTCE_ENDDE,'-','') >= TO_CHAR(SYSDATE, 'YYYYMMDD') THEN '2' ELSE '3' END AS STATUS FROM tb_bbs_estn a WHERE DELETE_CODE = '0' AND ntt_id < #nttId# AND bbs_id = #bbsId#
ORDER BY FRST_REGIST_PNTTM DESC ) a )a WHERE A.RNUM = 1

SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
SELECT a.NTT_ID , a.NTT_SJ , a.NTT_CN , a.IMG_DESC_CN , a.BBS_ID , a.OPTION1 , a.FRST_REGIST_PNTTM as REG_DATE , TO_CHAR(a.FRST_REGIST_PNTTM, 'YYYY.MM.DD') as FRST_REGISTER_PNTTM , a.ATCH_FILE_ID , c.FILE_SN , a.REPLY_AT , a.SECRET_AT , case WHEN a.FRST_REGIST_PNTTM >= SYSDATE-1 then 'Y' ELSE 'N' END AS NEW_YN FROM TB_BBS_ESTN a LEFT JOIN TC_COMMNFILE_DETAIL c on a.ATCH_FILE_ID = c.ATCH_FILE_ID WHERE a.DELETE_CODE = '0' AND a.BBS_ID = #bbsId# AND a.USE_AT = 'Y' AND a.OPTION1 IS NOT NULL ORDER BY REG_DATE DESC
) ALL_LIST ) WHERE RNUM > '0' AND RNUM <= '0' + #mainRecordCountPerPage#

/*optionalBoardDAO.selectMainONVBoardArticleList*/
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
SELECT NTT_ID , USER_ID , USER_NM , WRITER_NM , POLL_NAME , TO_CHAR(REG_DATE, 'YYYY-MM-DD') AS REG_DATE , POLL_AREA , DECISION , POLL_VIEW_DATE , MODIFY_REQUEST_DATE
FROM TB_BBS_OPINION_POLL WHERE DELETE_CODE = 0 AND SAVE_TYPE = #saveTp# ORDER BY REG_DATE DESC
) ALL_LIST ) WHERE RNUM > '0' AND RNUM <= '0' + #mainRecordCountPerPage#
