/* HkCultureResveDAO.listFclty */
SELECT TB.*, FN_CMMNCODE_NM('COM061',RESVE_SE) AS RESVE_SE_NM, FN_CMMNCODE_NM('COM172',AREA_SE) AS AREA_SE_NM, FN_CMMNCODE_NM('COM172',LC_SE) AS LC_SE_NM, FN_CMMNCODE_NM_EN('COM172',LC_SE) AS LC_SE_NM_EN, FN_CMMNCODE_NM('COM172',FCLTY_SE) AS FCLTY_SE_NM FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
SELECT A.FCLTY_SN, A.RESVE_SE, FN_CMMNCODE_NM('COM061',A.RESVE_SE) AS RESVE_SE_NM, A.AREA_SE, FN_CMMNCODE_NM('COM172',A.AREA_SE) AS AREA_SE_NM, A.LC_SE, FN_CMMNCODE_NM('COM172',A.LC_SE) AS LC_SE_NM, FN_CMMNCODE_NM_EN('COM172',A.LC_SE) AS LC_SE_NM_EN, A.FCLTY_SE, FN_CMMNCODE_NM('COM172',A.FCLTY_SE) AS FCLTY_SE_NM, A.SPCE_NM, A.SPCE_NM_EN, A.RESVE_POSBL_AT, DECODE(A.RESVE_SE,'I',1,DECODE(RESVE_POSBL_AT,'Y',0,1)) AS RESVE_POSBL_ORDR, A.USE_AT, A.AR, A.NMPR, A.PRPOS, A.PRPOS_EN, A.EQPMN_USE_AT, A.RESVE_IMPRTY_RESN, A.ATCH_FILE_ID, A.WKEND_RESVE_POLSBL_AT, TO_CHAR(A.FRST_REGIST_PNTTM, 'YYYY-MM-DD HH24:MI:SS') AS FRST_REGIST_PNTTM, A.FRST_REGISTER_ID, TO_CHAR(A.LAST_UPDT_PNTTM, 'YYYY-MM-DD HH24:MI:SS') AS LAST_UPDT_PNTTM, A.LAST_UPDUSR_ID, A.DELETE_AT, A.OPEN_AT FROM TN_CULT_FCLTY A WHERE DELETE_AT = 'N'
ORDER BY RESVE_POSBL_ORDR, LC_SE ASC, SPCE_NM ASC
) ALL_LIST ) TB WHERE RNUM > #firstIndex# AND RNUM <= #firstIndex# + #recordCountPerPage#


/* HkCultureResveDAO.istFcltyCnt */
SELECT COUNT(*) FROM TN_CULT_FCLTY A
WHERE DELETE_AT = 'N'

/* HkCultureResveDAO.listFcltySpceNm */ SELECT A.FCLTY_SN, A.SPCE_NM, A.SPCE_NM_EN FROM TN_CULT_FCLTY A WHERE A.DELETE_AT = 'N' AND A.AREA_SE = #areaSe# AND A.LC_SE = #lcSe# AND A.FCLTY_SE = #fcltySe# AND A.RESVE_POSBL_AT = 'Y' AND A.USE_AT = 'Y'
AND RESVE_SE IN ('O','A')
ORDER BY A.SPCE_NM ASC

/* HkCultureResveDAO.listEqpmnByFclty */ SELECT A.EQPMN_SN, A.EQPMN_NM, A.EQPMN_NM_EN, A.EQPMN_SE, FN_CMMNCODE_NM('COM147',A.EQPMN_SE) AS EQPMN_SE_NM, A.RESVE_SE, FN_CMMNCODE_NM('COM061',A.RESVE_SE) AS RESVE_SE_NM, A.RESVE_POSBL_AT, A.RESVE_IMPRTY_RESN, A.USE_AT, A.DTLS_INFO, A.DTLS_INFO_EN, A.ATCH_FILE_ID, TO_CHAR(A.FRST_REGIST_PNTTM, 'YYYY-MM-DD HH24:MI:SS') AS FRST_REGIST_PNTTM, A.FRST_REGISTER_ID, TO_CHAR(A.LAST_UPDT_PNTTM, 'YYYY-MM-DD HH24:MI:SS') AS LAST_UPDT_PNTTM, A.LAST_UPDUSR_ID, A.DELETE_AT, (SELECT COUNT(*) FROM TN_CULT_EQPMN_DETAIL WHERE EQPMN_SN = A.EQPMN_SN AND DELETE_AT = '0' ) AS EQPMN_QY, (SELECT TO_CHAR(WM_CONCAT(EQPMN_DETAIL_SN)) FROM TN_CULT_EQPMN_DETAIL WHERE EQPMN_SN = A.EQPMN_SN AND DELETE_AT = '0' ) AS EQPMN_DETAIL_SN_LIST, B.FCLTY_SN FROM TN_CULT_EQPMN A, TN_CULT_FCLTY_EQPMN B, TN_CULT_FCLTY C WHERE A.DELETE_AT = 'N' AND A.USE_AT = 'Y' AND A.RESVE_POSBL_AT='Y'
AND A.OPEN_AT='Y'
AND A.EQPMN_SN = B.EQPMN_SN AND B.FCLTY_SN = #fcltySn# AND B.FCLTY_SN = C.FCLTY_SN AND C.EQPMN_USE_AT = 'Y' ORDER BY A.EQPMN_NM ASC

/* HkCultureResveDAO.listEqpmn */
SELECT TB.*, FN_CMMNCODE_NM('COM147',EQPMN_SE) AS EQPMN_SE_NM, FN_CMMNCODE_NM('COM061',RESVE_SE) AS RESVE_SE_NM FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
SELECT A.EQPMN_SN, A.EQPMN_NM, A.EQPMN_NM_EN, A.EQPMN_SE, FN_CMMNCODE_NM('COM147',A.EQPMN_SE) AS EQPMN_SE_NM, A.RESVE_SE, FN_CMMNCODE_NM('COM061',A.RESVE_SE) AS RESVE_SE_NM, A.RESVE_POSBL_AT, A.RESVE_IMPRTY_RESN, A.USE_AT, A.DTLS_INFO, A.DTLS_INFO_EN, A.ATCH_FILE_ID, TO_CHAR(A.FRST_REGIST_PNTTM, 'YYYY-MM-DD HH24:MI:SS') AS FRST_REGIST_PNTTM, A.FRST_REGISTER_ID, TO_CHAR(A.LAST_UPDT_PNTTM, 'YYYY-MM-DD HH24:MI:SS') AS LAST_UPDT_PNTTM, A.LAST_UPDUSR_ID, A.DELETE_AT, A.OPEN_AT, (SELECT COUNT(*) FROM TN_CULT_EQPMN_DETAIL WHERE EQPMN_SN = A.EQPMN_SN AND DELETE_AT = '0' ) AS EQPMN_QY FROM TN_CULT_EQPMN A WHERE DELETE_AT = 'N'
ORDER BY A.FRST_REGIST_PNTTM DESC
) ALL_LIST ) TB WHERE RNUM > #firstIndex# AND RNUM <= #firstIndex# + #recordCountPerPage#


/* HkCultureResveDAO.listEqpmnCnt */
SELECT COUNT(*) FROM TN_CULT_EQPMN A
WHERE DELETE_AT = 'N'

/* HkCultureResveDAO.listResveSttus */ SELECT TB.*, FN_CMMNCODE_NM('COM061',RESVE_SE) AS RESVE_SE_NM, FN_CMMNCODE_NM('COM172',AREA_SE) AS AREA_SE_NM, FN_CMMNCODE_NM('COM172',LC_SE) AS LC_SE_NM, FN_CMMNCODE_NM('COM172',FCLTY_SE) AS FCLTY_SE_NM, FN_CMMNCODE_NM('COM081',AGE_SE) AS AGE_SE_NM, FN_CMMNCODE_NM('COM056',JOB_SE) AS JOB_SE_NM, FN_CMMNCODE_NM('COM063',RESDNC_SE) AS RESDNC_SE_NM, FN_CMMNCODE_NM('COM173',USE_PURPOSE_SE) AS USE_PURPOSE_SE_NM, FN_CMMNCODE_NM('COM146',RESVE_RECPTN_SE) AS RESVE_RECPTN_SE_NM, FN_CMMNCODE_NM('COM071',RESVE_STTUS) AS RESVE_STTUS_NM, FN_CMMNCODE_NM('COM073',USE_AT) AS USE_AT_NM FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
SELECT B.RESVE_SE, B.AREA_SE, B.LC_SE, B.FCLTY_SE, B.SPCE_NM, B.SPCE_NM_EN, A.FCLTY_SN, A.RESVE_SN, A.USER_SN, A.RESVE_NM, A.AGE_SE, A.JOB_SE, A.RESDNC_SE, A.USE_NMPR, A.USE_PURPOSE_SE, A.USE_PURPOSE_CN, A.RESVE_RECPTN_SE, TO_CHAR(A.RESVE_DT, 'YYYY-MM-DD') AS RESVE_DT, A.RESVE_STTUS, DECODE(A.RESVE_STTUS,'01',1,'03',2,'02',3,'04',4,'05',5,6) AS RESVE_OROR_SORT, CASE WHEN TRUNC(A.RESVE_DT)+ (1/24)*(A.RESVE_END_TIME+1) <= SYSDATE AND A.USE_AT = 'W' THEN 'N' ELSE A.USE_AT END AS USE_AT, A.RESVE_NO, A.RESVE_BEGIN_TIME, A.RESVE_END_TIME, TO_CHAR(A.FRST_REGIST_PNTTM, 'YYYY-MM-DD HH24:MI:SS') AS FRST_REGIST_PNTTM, A.FRST_REGISTER_ID, TO_CHAR(A.LAST_UPDT_PNTTM, 'YYYY-MM-DD HH24:MI:SS') AS LAST_UPDT_PNTTM, A.LAST_UPDUSR_ID, A.DELETE_AT, ( SELECT TO_CHAR(WM_CONCAT(EQPMN_NM || '('|| EQPMN_QY || ')')) FROM ( SELECT CC.RESVE_SN, BB.EQPMN_NM, COUNT(AA.EQPMN_DETAIL_SN) AS EQPMN_QY FROM TN_CULT_EQPMN_DETAIL AA, TN_CULT_EQPMN BB, TN_CULT_RESVE_EQPMN CC WHERE AA.EQPMN_SN = BB.EQPMN_SN AND AA.EQPMN_DETAIL_SN = CC.EQPMN_DETAIL_SN GROUP BY CC.RESVE_SN, BB.EQPMN_NM ORDER BY BB.EQPMN_NM ASC ) WHERE RESVE_SN = A.RESVE_SN
) AS EQPMN_NMS, CASE WHEN TRUNC(A.RESVE_DT-1) >= TRUNC(SYSDATE) THEN 'Y' ELSE 'N' END AS CANCEL_USE_AT, A.ADM_CMMNT FROM TN_CULT_FCLTY_RESVE A, TN_CULT_FCLTY B WHERE A.DELETE_AT = 'N' AND A.FCLTY_SN = B.FCLTY_SN(+)
ORDER BY RESVE_OROR_SORT ASC, A.RESVE_DT DESC, A.RESVE_BEGIN_TIME ASC
) ALL_LIST ) TB WHERE RNUM > #firstIndex# AND RNUM <= #firstIndex# + #recordCountPerPage#

/* HkCultureResveDAO.listExcelResveSttus */ SELECT A.RESVE_NO, FN_CMMNCODE_NM('COM061',B.RESVE_SE) AS RESVE_SE_NM, FN_CMMNCODE_NM('COM172',B.AREA_SE) AS AREA_SE_NM, FN_CMMNCODE_NM('COM172',B.LC_SE) AS LC_SE_NM, FN_CMMNCODE_NM('COM172',B.FCLTY_SE) AS FCLTY_SE_NM, B.SPCE_NM, B.SPCE_NM_EN, /* ( SELECT TO_CHAR(WM_CONCAT(EQPMN_NM || '('|| EQPMN_QY || ')')) FROM ( SELECT CC.RESVE_SN, BB.EQPMN_NM, COUNT(AA.EQPMN_DETAIL_SN) AS EQPMN_QY FROM TN_CULT_EQPMN_DETAIL AA, TN_CULT_EQPMN BB, TN_CULT_RESVE_EQPMN CC WHERE AA.EQPMN_SN = BB.EQPMN_SN AND AA.EQPMN_DETAIL_SN = CC.EQPMN_DETAIL_SN GROUP BY CC.RESVE_SN, BB.EQPMN_NM ORDER BY BB.EQPMN_NM ASC ) WHERE RESVE_SN = A.RESVE_SN
) AS EQPMN_NMS, */ A.RESVE_NM, TO_CHAR(A.RESVE_DT, 'YYYY-MM-DD') AS RESVE_DT, LPAD(A.RESVE_BEGIN_TIME,2,'0') || ':00 ~' || LPAD(A.RESVE_END_TIME,2,'0') || ':59' AS RESVE_TIME, FN_CMMNCODE_NM('COM071',A.RESVE_STTUS) AS RESVE_STTUS_NM, A.MBTLNUM, A.EMAIL, FN_CMMNCODE_NM('COM063',A.RESDNC_SE) AS RESDNC_SE_NM, A.USE_NMPR, FN_CMMNCODE_NM('COM146',A.RESVE_RECPTN_SE) AS RESVE_RECPTN_SE_NM, FN_CMMNCODE_NM('COM173',A.USE_PURPOSE_SE) AS USE_PURPOSE_SE_NM, A.USE_PURPOSE_CN, CASE WHEN TRUNC(A.RESVE_DT)+ (1/24)*(A.RESVE_END_TIME+1) <= SYSDATE AND A.USE_AT = 'W' THEN FN_CMMNCODE_NM('COM073','N') ELSE FN_CMMNCODE_NM('COM073',A.USE_AT) END AS USE_AT_NM, TO_CHAR(A.FRST_REGIST_PNTTM, 'YYYY-MM-DD HH24:MI:SS') AS FRST_REGIST_PNTTM, A.ADM_CMMNT FROM TN_CULT_FCLTY_RESVE A, TN_CULT_FCLTY B WHERE A.DELETE_AT = 'N' AND A.FCLTY_SN = B.FCLTY_SN(+)
ORDER BY A.RESVE_DT DESC

