sqlMap.select:
/* HkCultureResveDAO.listStatsFclty */ SELECT NVL(FN_CMMNCODE_NM('COM172',B.AREA_SE),'총계') AS AREA_SE_NM, NVL(FN_CMMNCODE_NM('COM172',B.LC_SE),'총계') AS LC_SE_NM, NVL(FN_CMMNCODE_NM('COM172',B.FCLTY_SE),'총계') AS FCLTY_SE_NM, NVL(B.SPCE_NM,'총계') AS SPCE_NM, SUM(DECODE(A.RESVE_STTUS,'01',1,0)) AS STTUS01, SUM(DECODE(A.RESVE_STTUS,'02',1,0)) AS STTUS02, SUM(DECODE(A.RESVE_STTUS,'04',1,0)) AS STTUS04, SUM(DECODE(A.RESVE_STTUS,'03',1,0)) AS STTUS03, SUM(DECODE(A.RESVE_STTUS,'05',1,0)) AS STTUS05, COUNT(*) AS STTUSALL, SUM(DECODE(A.RESVE_STTUS,'01',DECODE(A.USE_AT,'Y',1,0),0)) AS USE_YES, SUM(DECODE(A.RESVE_STTUS,'01',CASE WHEN TRUNC(A.RESVE_DT)+ (1/24)*(A.RESVE_END_TIME+1) > SYSDATE AND A.USE_AT = 'W' THEN 1 ELSE 0 END,0)) AS USE_WAIT, SUM(DECODE(A.RESVE_STTUS,'01',CASE WHEN TRUNC(A.RESVE_DT)+ (1/24)*(A.RESVE_END_TIME+1) <= SYSDATE AND A.USE_AT = 'W' THEN 1 ELSE 0 END,0)) AS USE_NO, COUNT(*) AS STTUSALL2
FROM TN_CULT_FCLTY_RESVE A, TN_CULT_FCLTY B WHERE A.DELETE_AT = 'N' AND A.FCLTY_SN = B.FCLTY_SN(+) AND A.DELETE_AT = 'N'
GROUP BY GROUPING SETS ((B.AREA_SE, B.LC_SE,B.FCLTY_SE, B.SPCE_NM),()) ORDER BY B.AREA_SE, B.LC_SE,B.FCLTY_SE, B.SPCE_NM

/* HkCultureResveDAO.listStatsUsePurps */ SELECT NVL(FN_CMMNCODE_NM('COM172',B.AREA_SE),'총계') AS AREA_SE_NM, NVL(FN_CMMNCODE_NM('COM172',B.LC_SE),'총계') AS LC_SE_NM, NVL(FN_CMMNCODE_NM('COM172',B.FCLTY_SE),'총계') AS FCLTY_SE_NM, NVL(B.SPCE_NM,'총계') AS SPCE_NM, NVL(SUM(DECODE(A.USE_PURPOSE_SE,'01',1,0)),0) AS PURPS01, NVL(SUM(DECODE(A.USE_PURPOSE_SE,'02',1,0)),0) AS PURPS02, NVL(SUM(DECODE(A.USE_PURPOSE_SE,'04',1,0)),0) AS PURPS04, NVL(SUM(DECODE(A.USE_PURPOSE_SE,'03',1,0)),0) AS PURPS03, NVL(SUM(DECODE(A.USE_PURPOSE_SE,'05',1,0)),0) AS PURPS05, NVL(SUM(DECODE(A.USE_PURPOSE_SE,NULL,0,1)),0) AS PURPSALL
FROM TN_CULT_FCLTY_RESVE A, TN_CULT_FCLTY B WHERE A.DELETE_AT = 'N' AND A.FCLTY_SN = B.FCLTY_SN(+) AND A.DELETE_AT = 'N'
GROUP BY GROUPING SETS ((B.AREA_SE, B.LC_SE,B.FCLTY_SE, B.SPCE_NM),()) ORDER BY B.AREA_SE, B.LC_SE,B.FCLTY_SE, B.SPCE_NM

/* HkCultureResveDAO.listStatsFcltyByMonth */ SELECT DECODE(RESVE_YYYYMM,NULL,'총합계',TO_CHAR(RESVE_YYYYMM, 'yy') || '년 ' || TO_CHAR(RESVE_YYYYMM, 'MM') || '월') AS RESVE_YYYYMM, NVL(LC_SE,'0000') AS LC_SE, NVL(FCLTY_SN,0) AS FCLTY_SN, FN_CMMNCODE_NM('COM172',LC_SE) || DECODE(SPCE_NM,NULL,DECODE(LC_SE,NULL,'',' > '),' > ') || DECODE(FCLTY_SN,NULL,DECODE(LC_SE,NULL,'총합계','전체'),SPCE_NM) AS SPCE_NM, USE_NMPR FROM ( SELECT B.FCLTY_SN, TRUNC(A.RESVE_DT,'MM') AS RESVE_YYYYMM, B.SPCE_NM, B.LC_SE, SUM(A.USE_NMPR) AS USE_NMPR FROM TN_CULT_FCLTY_RESVE A, TN_CULT_FCLTY B WHERE A.DELETE_AT = 'N' AND A.RESVE_STTUS = '02' AND A.FCLTY_SN = B.FCLTY_SN AND B.DELETE_AT = 'N'
TO_CHAR(A.RESVE_DT,'YYYYMM') BETWEEN #resveSdate# AND #resveEdate#
GROUP BY GROUPING SETS ((TRUNC(A.RESVE_DT,'MM'),B.LC_SE, B.FCLTY_SN, B.SPCE_NM),(TRUNC(A.RESVE_DT,'MM'),B.LC_SE),(TRUNC(A.RESVE_DT,'MM')),()) ) TB

/* HkCultureResveDAO.listSchdul */
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
SELECT A.EVENT_SN, TO_CHAR(A.EVENT_DT, 'YYYY-MM-DD') AS EVENT_DT, A.EVENT_BEGIN_TIME, A.EVENT_END_TIME, A.EVENT_NM, A.PLACE, TO_CHAR(A.FRST_REGIST_PNTTM, 'YYYY-MM-DD HH24:MI:SS') AS FRST_REGIST_PNTTM, A.FRST_REGISTER_ID, TO_CHAR(A.LAST_UPDT_PNTTM, 'YYYY-MM-DD HH24:MI:SS') AS LAST_UPDT_PNTTM, A.LAST_UPDUSR_ID, A.DELETE_AT, A.REF_RESVE_SN FROM TN_EVENT_SCHDUL A WHERE DELETE_AT = 'N'
ORDER BY A.FRST_REGIST_PNTTM DESC, EVENT_BEGIN_TIME ASC
) ALL_LIST ) WHERE RNUM > #firstIndex# AND RNUM <= #firstIndex# + #recordCountPerPage#


/* HkCultureResveDAO.listSchdulCnt */
SELECT COUNT(*) FROM TN_EVENT_SCHDUL A
WHERE DELETE_AT = 'N'
