sqlMap.select:
/* BasicBBSManageDAO.selectBoardArticleList */
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
SELECT a.NTT_ID , a.NTT_SJ , a.NTT_CN , a.FRST_REGISTER_ID , a.NTCR_NM , a.NTT_NO , a.INQIRE_CO , a.PARNTS , a.REPLY_AT , nvl(a.REPLY_LC,0) as REPLY_LC , a.USE_AT , a.ATCH_FILE_ID , a.BBS_ID , a.NTCE_BGNDE , a.NTCE_ENDDE , a.SECRET_AT as secret , NVL(c.COMMENT_CNT,0) AS comment_cnt , TO_CHAR(a.FRST_REGIST_PNTTM, 'YYYY-MM-DD') as FRST_REGISTER_PNTTM , a.FRST_REGIST_PNTTM as reg_date , a.SECTION , a.HTML_AT HTML_YN , a.DELETE_CODE DELCODE , a.DEPT_ID FROM $tableNm$ a LEFT OUTER JOIN ( SELECT BBS_ID, NTT_ID, COUNT(*) AS COMMENT_CNT FROM TB_BBS_CMMNT GROUP BY BBS_ID, NTT_ID ) c on a.BBS_ID = c.BBS_ID AND a.NTT_ID = c.NTT_ID WHERE a.BBS_ID = #bbsId#
ORDER BY a.PARNTS desc, a.REPLY_LC ASC
) ALL_LIST ) WHERE RNUM > #firstIndex# AND RNUM <= #firstIndex# + #recordCountPerPage#

SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
SELECT a.NTT_ID , a.NTT_SJ , a.NTT_CN , a.FRST_REGISTER_ID , a.NTCR_NM , a.NTT_NO , a.INQIRE_CO , a.PARNTS , a.REPLY_AT , a.REPLY_LC , a.USE_AT , a.ATCH_FILE_ID , a.BBS_ID , a.SECRET_AT SECRET , a.FRST_REGIST_PNTTM as reg_date , NVL(b.CNT,0) AS commentCnt ,TO_CHAR(a.FRST_REGIST_PNTTM, 'YYYY-MM-DD') as FRST_REGISTER_PNTTM , a.DEPT_ID FROM $tableNm$ a LEFT OUTER JOIN ( SELECT BBS_ID, NTT_ID, COUNT(*) AS CNT FROM BBSCOMMENTS GROUP BY BBS_ID, NTT_ID ) b ON b.BBS_ID = a.BBS_ID AND b.NTT_ID = a.NTT_ID WHERE a.BBS_ID = #bbsId# AND a.DELETE_CODE = '0' AND a.PARNTS = #nttId# ORDER BY a.FRST_REGIST_PNTTM DESC
) ALL_LIST ) WHERE RNUM > #firstIndex# AND RNUM <= #firstIndex# + #recordCountPerPage#

/* BasicBBSManageDAO.selectPrevNexArticle */
SELECT ntt_id, nvl(prev_ntt_id, 0) as prev_ntt_id, nvl(next_ntt_id, 0) as next_ntt_id, prev_ntt_sj, next_ntt_sj FROM ( SELECT NTT_ID , LEAD(ntt_id, 1, '') OVER (ORDER BY ntt_id DESC) AS prev_ntt_id , LAG(ntt_id, 1, '') OVER (ORDER BY ntt_id DESC ) AS next_ntt_id , LEAD(ntt_sj, 1, '') OVER (ORDER BY ntt_id DESC) AS prev_ntt_sj , LAG(ntt_sj, 1, '') OVER (ORDER BY ntt_id DESC ) AS next_ntt_sj FROM $tableNm$ a WHERE BBS_ID = #bbsId# AND a.DELETE_CODE = '0'
) WHERE ntt_id = #nttId#

/* BasicBBSManageDAO.prevSelectPrevNexArticle */
SELECT aa.NTT_TYPE, aa.NTT_ID, aa.NTT_SJ FROM ( SELECT 'NEXT' AS ntt_type ,a.NTT_ID ,a.NTT_SJ FROM $tableNm$ a WHERE a.BBS_ID = #bbsId# AND a.DELETE_CODE = '0' AND a.NTT_ID IN ( SELECT MIN(NTT_ID) FROM $tableNm$ WHERE BBS_ID = #bbsId# AND DELETE_CODE = '0' AND NTT_ID > #nttId#
)
UNION ALL
SELECT 'PREV' AS ntt_type ,a.NTT_ID ,a.NTT_SJ FROM $tableNm$ a WHERE a.BBS_ID = #bbsId# AND a.DELETE_CODE = '0' AND a.NTT_ID IN ( SELECT MAX(NTT_ID) FROM $tableNm$ WHERE BBS_ID = #bbsId# AND DELETE_CODE = '0' AND NTT_ID < #nttId#
) ) aa

/* BasicBBSManageDAO.selectB0000006BoardArticleList */
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
SELECT a.NTT_ID , a.NTT_SJ , a.NTT_CN , a.NTCR_NM , a.INQIRE_CO , a.USE_AT , a.BBS_ID ,TO_CHAR(a.FRST_REGIST_PNTTM, 'YYYY-MM-DD') as FRST_REGISTER_PNTTM ,a.BBS_SE GUBUN ,a.HTML_AT HTML_YN ,a.DELETE_CODE DELCODE FROM $tableNm$ a WHERE a.BBS_ID = #bbsId#
ORDER BY a.FRST_REGIST_PNTTM DESC
) ALL_LIST ) WHERE RNUM > #firstIndex# AND RNUM <= #firstIndex# + #recordCountPerPage#
